<?phpclass Auth{    function Auth(){        //    }	    function isLoggedIn(){        if(Auth::checkSession() && Auth::checkLocalCookie()){            Auth::setLocalCookie();            return true;        } elseif(Auth::checkSession() && !Auth::checkLocalCookie()) {	        Auth::setLocalCookie();            return true;        } elseif(!Auth::checkSession() && Auth::checkLocalCookie()) {            Auth::setLocalCookie();            Auth::setSession();            return true;        } else {        	return false;        }    }		    function checkLocalCookie(){        if(isset($_COOKIE['admin'])) return true;        else return false;    }	    function checkSession(){        if(isset($_SESSION['admin'])) return true;        else return false;    }	    function setSession(){        $_SESSION['admin'] = 1;    }	    function logOut(){    	setcookie ( 'admin', '0', time()-13600, '/');    	$_COOKIE = array();    	$_SESSION = array();    }	    function setLocalCookie(){        $expire_time = time()+60*60*24*30;        setcookie ('admin', '1', $expire_time, '/');    }	    function refreshLocalCookie($writer_id){        $expire_time = time()+60*60*24*30;        setcookie ( 'admin', '1', $expire_time, '/');    }    }?>