<?phpclass Debug{	var $debug_on = false;		function Debug($debug_on){		if($debug_on) print "Debug Mode On";			}		function debugStatus($debug_on){		$this->debug_on = $debug_on;	}		function printr($value, $die = false, $return = false){				if(!$this->debug_on)return false;								if($return) ob_start();				print "<pre style='background-color: white; border: 1px solid black; color: black; text-align: left; position:relative; z-index: 100000;'>";		print_r($value);		print "</pre>";				if($return){			$str = ob_get_contents();			ob_end_clean();			return $str;		}				if($die) die('halted by debug');	}		function query_fail($query, $die = false){		$this->printr(mysql_error());		$this->printr($query);		if($die) die();	}}?>