<?phprequire_once 'include.php';if(!empty($_POST)){	$html_body = '<html><body>';	foreach($_POST as $k => $v){		$html_body .= $k . ': ' . $v . "<br>";	}	$html_body .= '</body></html>';	include_once 'classes/class.phpmailer.php';	$mail = new PHPMailer(true); //New instance, with exceptions enabled	$mail->AddReplyTo($_POST['email'], $_POST['name']);	$mail->From       = "noreply@motoreach.com";	$mail->FromName   = "MotoReach";	$mail->AddAddress('info@motoreach.com', 'Evelyn Curry');	$mail->Subject  = 'Mail from MotoReach Contact Form';	$mail->AltBody    = "To view the message, please use an HTML compatible email viewer!"; // optional, comment out and test	$mail->WordWrap   = 80; // set word wrap	$mail->MsgHTML($html_body);		$mail->IsHTML(true); // send as HTML	$mail->Send();	}$main_content = new Template('contact');$miniform = new Template('basicvehicle');$main_content->set('miniform', $miniform->fetch());$template->set('content', $main_content->fetch());echo $template->fetch();?>