;		$ct += $d['vehicles'];		} 		?>	<tr style="color: #FF7F00; font-weight: bold;">		<td></td>		<td></td>		<td></td>		<td><?php echo $i ?> new dealerships</td>		<td><?php echo $ct ?> vehicles listed</td>	</tr>	</table>	<?php } else { ?>	There are no new dealerships for this period.	<?php } ?><?php } ?><h3><?php echo $headers['vehicles_period']?></h3><?php if(!isset($_GET['user_id'])){	if(!empty($vehicles)){ 		$i = 0;		$ct = 0;		?>		<table class="admin_list">		<tr>			<th><a>Dealership</a></th>			<th><a>Name</a></th>			<th><a>Vehicles</a></th>		</tr>		<?php 		foreach($vehicles as $v){ 			if(isset($v['total'])) continue;			?>		<tr style="background-color: #<?php echo ($i%2?'EBEBEB':'FFFFFF')?>">			<td><a href="?user_id=<?php echo $v['user_id'] . (isset($_GET['from_date'])?'&from_date='.$_GET['from_date']:'') . (isset($_GET['to_date'])?'&to_date='.$_GET['to_date']:'') ?>"><?php echo $v['dealership_name']?></a></td>			<td><?php echo $v['fullname']?></td>			<td><?php echo $v['vehicles']?></td>		</tr>		<?php 			$i++;			$ct += $v['vehicles'];		} 		?>	<tr style="color: #FF7F00; font-weight: bold;">		<td></td>		<td></td>		<td><?php echo $ct ?></td>	</tr>	</table>	<?php 	}} else {	if(!empty($vehicles)){ 	$i = 0;	$ct = 0;	?>	<table class="admin_list">	<tr>		<th><a>Dealership</a></th>		<th><a>Vehicle</a></th>		<th><a>Price</a></th>		<th><a>Date</a></th>		<th><a>Requests</a></th>	</tr>	<?php 	foreach($vehicles as $v){ 		if(isset($v['total'])) continue;			?>		<tr style="background-color: #<?php echo ($i%2?'EBEBEB':'FFFFFF')?>">			<td><?php echo $v['dealership_name']?></td>			<td><?php echo $v['make']?> <?php echo $v['model'] ?></td>			<td>$<?php echo $v['startprice']?></td>			<td><?php echo date('d F Y', $v['datesubmitted'])?></td>			<td><?php echo $v['requests']?></td>		</tr>		<?php 		$i++;		$ct += $v['requests'];	} 	?>	<tr style="color: #FF7F00; font-weight: bold;">		<td></td>		<td></td>		<td></td>		<td><?php echo $i ?> vehicles</td>		<td><?php echo $ct ?> requests</td>	</tr>	</table>	<?php 	}}?><br /><br /><?php if(!isset($_GET['user_id'])){?><h3><?php echo $headers['requests_period']?></h3><?php if(!empty($requests)){ $i = 0;?><table class="admin_list"><tr>	<th><a>Dealership</a></th>	<th><a>Buy Request</a></th>	<th><a>Sell Request</a></th></tr><?php $total['buy'] = 0;$total['sell'] = 0;foreach($requests as $k => $v){ if(isset($v['total'])) continue;		?><tr style="background-color: #<?php echo ($i%2?'EBEBEB':'FFFFFF')?>">	<td><?php echo $k?></td>	<td><?php echo (int)@$v['buyer_requests']?></td>	<td><?php echo (int)@$v['seller_requests']?></td></tr><?php 	$total['buy'] += @$v['buyer_requests'];	$total['sell'] += @$v['seller_requests'];$i++;} ?><tr style="color: #FF7F00; font-weight: bold;">	<td>Total</td>	<td><?php echo $total['buy']?></td>	<td><?php echo $total['sell']?></td></tr></table><?php } ?><?php }//$GLOBALS['debug']->printr($total);//$GLOBALS['debug']->printr($seller_requests);//$GLOBALS['debug']->printr($buyer_requests);?></div>