?date(DAY_MONTH_YEAR, $v['dateentered']):(int)@$v['listed']?></td>	<td><?php echo (int)@$v['requests']?></td>	<?php if(!isset($_GET['id'])){ ?><td><?php echo (int)@$v['requests_made']?></td><?php } ?></tr><?php}?><tr style="background-color: #ffc994;">	<td></td>	<td><?php echo isset($_GET['id'])?'':$listed?></td>	<td><?php echo $requests ?></td>	<?php if(!isset($_GET['id'])){ ?><td><?php echo $requests_made?></td><?php } ?></tr><?php} else {}?></table></div>